int	key_press(int keycode, t_cub *cub)
{
	if (keycode == ESC)
		exit(0);
	cub->keys[keycode] = 1;
	return (0);
}

int	key_release(int keycode, t_cub *cub)
{
	cub->keys[keycode] = 0;
	return (0);
}

int	mouse_move(int x, int y, t_cub *cub)
{
	double	angle;
	double	diff;

	(void) y;
	diff = (cub->width / 2) - x;
	angle = (diff / (cub->width / 2.0)) * (cub->player.fov / 2.0);
	cub->player.dir = vec2_rotate(cub->player.dir, -angle);
	mlx_mouse_move(cub->window, cub->width / 2, cub->height / 2);
	return (0);
}

void	handle_keys(t_cub *cub)
{
	t_vec2	d;
	t_vec2	p;
	int		f;
	double	bounds;

	d = cub->player.dir;
	p = cub->player.pos;
	bounds = 0.3;
	if (cub->keys[K_W] || cub->keys[K_S])
	{
		f = !cub->keys[K_S] * 2 - 1;
		if (map[(int)(p.y)][(int)(p.x + d.x * bounds * f)] == 0)
			cub->player.pos.x += d.x * 0.05 * f;
		if (map[(int)(p.y + d.y * bounds * f)][(int)(p.x)] == 0)
			cub->player.pos.y += d.y * 0.05 * f;
	}
	if (cub->keys[K_A] || cub->keys[K_D])
	{
		d = vec2_rotate(d, PI_2);
		f = !cub->keys[K_A] * 2 - 1;
		if (map[(int)(p.y)][(int)(p.x + d.x * bounds * f)] == 0)
			cub->player.pos.x += d.x * 0.05 * f;
		if (map[(int)(p.y + d.y * bounds * f)][(int)(p.x)] == 0)
			cub->player.pos.y += d.y * 0.05 * f;
	}
	if (cub->keys[ARROW_LEFT] || cub->keys[ARROW_RIGHT])
		cub->player.dir = vec2_rotate(cub->player.dir, PI / 90 * (!cub->keys[ARROW_LEFT] * 2 - 1));
}

void	init_cub(t_cub *cub)
{
	cub->mlx = mlx_init();
	cub->width = 1280;
	cub->height = 720;
	cub->map_width = 30;
	cub->map_height = 15;
	cub->window = mlx_new_window(cub->mlx, cub->width, cub->height, "Cub3D");
	cub->frame = new_image(cub->mlx, cub->width, cub->height);
	cub->minimap = new_image(cub->mlx, cub->width / 4, cub->height / 4);
	cub->player.pos.x = 1.5;
	cub->player.pos.y = 1.5;
	cub->player.dir.x = 1;
	cub->player.dir.y = 0;
	cub->player.fov = rad(60);
	memset(cub->keys, 0, sizeof(int) * 512);
}


t_vec2	h_ray(t_cub *cub, t_vec2 pos, t_vec2 dir, int size)
{
	// t_vec2	prev_hit;
	t_vec2	hit;
	double	dir_angle;
	int		dof;
	(void) size;

	// prev_hit.x = pos.x;
	// prev_hit.y = pos.y;
	hit.x = DBL_MAX;
	hit.y = DBL_MAX;
	dir_angle = vec2_angle(dir);
	if (sin(dir_angle) > 0.001)
		hit = (t_vec2){fmod(pos.y, 1) / tan(dir_angle) + pos.x, (int)pos.y - 0.001};
	else if (sin(dir_angle) < -0.001)
		hit = (t_vec2){(1 - fmod(pos.y, 1)) / tan(-dir_angle) + pos.x, (int)pos.y + 1};
	else
		return (hit);
	dof = 0;
	while (dof < cub->map_width)
	{
		// draw_line(cub->minimap, \
			// (t_point){prev_hit.x * size, prev_hit.y * size}, \
			// (t_point){hit.x * size, hit.y * size}, 0xFF00FF);
		// draw_square(cub->minimap, \
			// (t_point){hit.x * size - 1, hit.y * size - 1}, \
			// 3, 0xFF00FF);
		if (hit.x < 0 || hit.x >= cub->map_width || \
			hit.y < 0 || hit.y >= cub->map_height || \
			map[(int)hit.y][(int)hit.x])
			return (hit);
		// if (map[(int)hit.y][(int)hit.x] || (hit.x < 0 || hit.x >= cub->map_width) \
		// 								|| (hit.y < 0 || hit.y >= cub->map_height))
		// 	return (hit);
		else
		{
			// prev_hit.x = hit.x;
			// prev_hit.y = hit.y;
			hit.y += (sin(dir_angle) < -0.001) * 2 - 1;
			hit.x -= ((sin(dir_angle) < -0.001) * 2 - 1) / tan(dir_angle);
			dof++;
		}
	}
	return (hit);
}

t_vec2	v_ray(t_cub *cub, t_vec2 pos, t_vec2 dir, int size)
{
	// t_vec2	prev_hit;
	t_vec2	hit;
	double	dir_angle;
	int		dof;
	(void) size;

	// prev_hit.x = pos.x;
	// prev_hit.y = pos.y;
	hit.x = DBL_MAX;
	hit.y = DBL_MAX;
	dir_angle = vec2_angle(dir);
	if (cos(dir_angle) > 0.001)
		hit = (t_vec2){(int)pos.x + 1, (1 - fmod(pos.x, 1)) * tan(-dir_angle) + pos.y};
	else if (cos(dir_angle) < -0.001)
		hit = (t_vec2){(int)pos.x -0.001, fmod(pos.x, 1) * tan(dir_angle) + pos.y};
	else
		return (hit);
	dof = 0;
	while (dof < cub->map_width)
	{
		// draw_line(cub->minimap, \
		// 	(t_point){prev_hit.x * size, prev_hit.y * size}, \
		// 	(t_point){hit.x * size, hit.y * size}, 0xFFFF00);
		// draw_square(cub->minimap, \
		// 	(t_point){hit.x * size - 2, hit.y * size - 2}, \
		// 	5, 0xFFFF00);
		if (hit.x < 0 || hit.x >= cub->map_width || \
			hit.y < 0 || hit.y >= cub->map_height || \
			map[(int)hit.y][(int)hit.x])
			return (hit);
		// if (map[(int)hit.y][(int)hit.x] && (hit.x < 0 || hit.x >= cub->map_width) \
		// 								&& (hit.y < 0 || hit.y >= cub->map_height))
		else
		{
			// prev_hit.x = hit.x;
			// prev_hit.y = hit.y;
			hit.x -= (cos(dir_angle) < -0.001) * 2 - 1;
			hit.y += ((cos(dir_angle) < -0.001) * 2 - 1) * tan(dir_angle);
			dof++;
		}
	}
	return (hit);
}

t_side	get_side(t_vec2 pos, t_vec2 dir, t_vec2 h_ray, t_vec2 v_ray)
{
	double	dist_h;
	double	dist_v;
	double	angle;

	dist_h = distance_fd(pos.x, pos.y, h_ray.x, h_ray.y);
	dist_v = distance_fd(pos.x, pos.y, v_ray.x, v_ray.y);
	angle = vec2_angle(dir);
	if (dist_h < dist_v)
	{
		if (sin(angle) > 0.001)
			return (NORTH);
		else
			return (SOUTH);
	}
	else
	{
		if (cos(angle) > 0.001)
			return (EAST);
		else
			return (WEST);
	}
}

t_ray	raycast(t_cub *cub, t_vec2 pos, t_vec2 dir)
{
	t_vec2	rays[2];
	double	dists[2];
	t_ray	ray;
	int		size;

	size = cub->height / 3 / 15;
	rays[0] = h_ray(cub, pos, dir, size);
	rays[1] = v_ray(cub, pos, dir, size);
	dists[0] = distance_fd(pos.x, pos.y, rays[0].x, rays[0].y);
	dists[1] = distance_fd(pos.x, pos.y, rays[1].x, rays[1].y);
	// draw_square(cub->minimap,
	// 	(t_point){\
	// 		(int)rays[0].x * size, \
	// 		(int)rays[0].y * size \
	// 	}, size, 0xFF00FF);
	// draw_square(cub->minimap,
	// 	(t_point){\
	// 		(int)rays[1].x * size, \
	// 		(int)rays[1].y * size \
	// 	}, size, 0xFFFF00);
	ray.pos = rays[dists[0] > dists[1]];
	ray.dist = dists[dists[0] > dists[1]];
	ray.side = get_side(pos, dir, rays[0], rays[1]);
	ray.value = map[(int)ray.pos.y][(int)ray.pos.x];
	return (ray);
}

void	draw_floor_sky(t_cub *cub)
{
	int	x;
	int	y;

	y = 0;
	while (y < cub->height)
	{
		x = 0;
		while (x < cub->width)
		{
			if (y < cub->height / 2)
				put_pixel(cub->frame, x, y, 0x5445FF);
			else
				put_pixel(cub->frame, x, y, 0x2E3136);
			x++;
		}
		y++;
	}
}

void	draw_minimap(t_cub *cub)
{
	t_ray		ray;
	t_vec2		vector;
	t_player	player;
	int			y;
	int			x;
	int			size;

	player = cub->player;
	size = 12;
	mlx_destroy_image(cub->mlx, cub->minimap.ptr);
	cub->minimap = new_image(cub->mlx, cub->map_width * size, cub->map_height * size);
	y = 0;
	while (y < cub->map_height)
	{
		x = 0;
		while (x < cub->map_width)
		{
			if (map[y][x])
				draw_square(cub->minimap, (t_point){x * size, y * size}, size, 0x220000FF);
			else
				draw_square(cub->minimap, (t_point){x * size, y * size}, size, 0x22000000);
			x++;
		}
		y++;
	}
	draw_square(cub->minimap,
		(t_point){\
			cub->player.pos.x * size - 2, \
			cub->player.pos.y * size - 2 \
		}, 5, 0xFFFF00);
	vector = vec2_rotate(cub->player.dir, cub->player.fov / 2.0);
	// printf("%d\n", vec2_angle(vector) == vec2_angle(cub->player.dir));
	draw_line(cub->minimap, \
		(t_point){player.pos.x * size, player.pos.y * size}, \
		(t_point){(player.pos.x + vector.x) * size, \
				(player.pos.y + vector.y) * size}, \
		0x00FF00);
	vector = vec2_rotate(cub->player.dir, cub->player.fov / -2.0);
	// printf("%d\n", vec2_angle(vector) == vec2_angle(cub->player.dir));
	draw_line(cub->minimap, \
		(t_point){player.pos.x * size, player.pos.y * size}, \
		(t_point){(player.pos.x + vector.x) * size, \
				(player.pos.y + vector.y) * size}, \
		0x00FF00);
	draw_line(cub->minimap, \
		(t_point){player.pos.x * size, player.pos.y * size}, \
		(t_point){(player.pos.x + player.dir.x) * size, \
				(player.pos.y + player.dir.y) * size}, \
		0xFF0000);
	ray = raycast(cub, cub->player.pos, cub->player.dir);
	draw_square(cub->minimap,
		(t_point){\
			(int)ray.pos.x * size, \
			(int)ray.pos.y * size \
		}, size, 0xFF0000);
	// printf("(%f, %f)\n", vector.x, vector.y);
}

void	draw_walls(t_cub *cub)
{
	t_ray	ray;
	t_vec2	dir;
	t_vec2	cur;
	t_vec2	tex;
	int		height;

	cur.x = 0;
	dir = vec2_rotate(cub->player.dir, cub->player.fov / -2.0);
	while (cur.x < cub->width)
	{
		ray = raycast(cub, cub->player.pos, dir);
		height = 64 / (ray.dist * cos(vec2_angle_from(cub->player.dir, dir))) * 20;
		cur.y = 0;
		while (cur.y < cub->height && cur.y < height)
		{
			tex.x = fmod(ray.pos.x, 1) * 64;
			if (ray.side % 2 == 0)
				tex.x = fmod(ray.pos.y, 1) * 64;
			tex.y = (cur.y + (height / 2.0 - cub->height / 2.0) * (height >= cub->height)) * 63 / height;
			if (ray.side % 2 == 0)
				put_pixel(cub->frame, cur.x, cur.y + (cub->height / 2.0 - height / 2.0) * (height < cub->height), wall_texture[(int)tex.y][(int)tex.x]);
			else
				put_pixel(cub->frame, cur.x, cur.y + (cub->height / 2.0 - height / 2.0) * (height < cub->height), darken(wall_texture[(int)tex.y][(int)tex.x], 0.5));
			cur.y++;
		}
		dir = vec2_rotate(dir, cub->player.fov / (double)cub->width);
		cur.x++;
	}
}

int	draw(t_cub *cub)
{
	clock_t	start = clock();
	clock_t	end;
	char	*str = NULL;
	char	*clock_str = NULL;

	asprintf(&str, "Angle: %.0f°\n", arc(vec2_angle(cub->player.dir)));
	handle_keys(cub);
	draw_floor_sky(cub);
	draw_minimap(cub);
	draw_walls(cub);
	end = clock();
	asprintf(&clock_str, "%.0f FPS\n", 1.0 / ((double)(end-start) / CLOCKS_PER_SEC));
	mlx_put_image_to_window(cub->mlx, cub->window, cub->frame.ptr, 0, 0);
	mlx_put_image_to_window(cub->mlx, cub->window, cub->minimap.ptr, 0, 0);
	mlx_string_put(cub->mlx, cub->window, 10, cub->height / 2 - 10, 0xFFFFFF, str);
	mlx_string_put(cub->mlx, cub->window, 10, cub->height / 2 - 20, 0xFFFFFF, clock_str);
	free(str);
	return (0);
}

int	main(void)
{
	t_cub	cub;

	init_cub(&cub);
	draw(&cub);
	mlx_do_key_autorepeaton(cub.mlx);
	// mlx_key_hook(cub.window, key_hook, &cub);
	mlx_hook(cub.window, 2, 0, key_press, &cub);
	mlx_hook(cub.window, 3, 0, key_release, &cub);
	mlx_hook(cub.window, 6, 0, mouse_move, &cub);
	mlx_mouse_hide();
	mlx_loop_hook(cub.mlx, draw, &cub);
	mlx_loop(cub.mlx);
	return (0);
}
